import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Notification from './components/Notification'
import Person from './components/Person'
import personService from './services/persons'


function Phonebook() {
  const [book, setBook] = useState([]);
  const [name, setName] = useState();
  const [number, setNumber] = useState();

  const [showAll, setShowAll] = useState(true)
  const [errorMessage, setErrorMessage] = useState(null)


  const fetch = async () => {
    const { data } = await axios.get('/api/persons')
    setBook(data);
  }

  useEffect(() => {
    fetch();
  }, [])

  const handleDelete = (e) => {
    axios.delete(`/api/persons/${e.target.id}`)
    fetch();
  }

  const handleSubmit = async () => {
    const checkPerson = book.find(person => person.name === name)
    if (checkPerson) {
      await axios.put(`/api/persons/${checkPerson.id}`, {
        name, 
        number
      })
    } else {
      await axios.post('/api/persons', {
        name, 
        number
      })
    }
    fetch();
  }

  return (
    <div className="App">
      <h1>Phone Book</h1>
      <Notification message={errorMessage} />
      <ul>
        {book.map((item, i) => 
          <li key={i}>{item.name} {item.number} <button id={item.id} onClick={handleDelete}>delete</button></li>
        )}
      </ul>

      <form onSubmit={handleSubmit}>
        <input onChange={(e) => setName(e.target.value)} type='text' placeholder='name'/>
        <input onChange={(e) => setNumber(e.target.value)} type='text' placeholder='number'/>
        <button type='submit'>Submit</button>
      </form>
    </div>
  );
}

export default Phonebook;
